// frontend/src/App.js

import React, { useState } from 'react';
import { Outlet, useLocation, useNavigate } from 'react-router-dom';
import LayoutHeader from './components/LayoutHeader';
import LayoutFooter from './components/LayoutFooter';
import CallToActionSection from './components/CallToActionSection';

function App() {
  const [language, setLanguage] = useState('es');
  const navigate = useNavigate(); // Hook para navegar entre páginas
  const location = useLocation();

  const handleToggleLanguage = () => {
    setLanguage((prevLang) => (prevLang === 'es' ? 'en' : 'es'));
  };

  const handleNavigate = (path) => {
    navigate(path);
  };

  return (
    <div className="bg-gray-900 text-gray-100 font-sans antialiased min-h-screen flex flex-col">
      <LayoutHeader
        language={language}
        onToggleLanguage={handleToggleLanguage}
        onNavigate={handleNavigate} // Pasamos la función de navegación al header
      />

      <main className="flex-grow">
        {/* React Router pondrá aquí HomePage o ServiceDetailPage */}
        <Outlet context={{ language }} /> 
      </main>

      {/* Solo mostramos CTA y Footer en la página de inicio */}
      {location.pathname === '/' && (
          <>
              <CallToActionSection language={language} onNavigate={handleNavigate} />
              <LayoutFooter language={language} />
          </>
      )}
    </div>
  );
}

export default App;
