                    {/* ... (código interno de la tarjeta) ... */}
                    <IconComponent className="w-8 h-8 ..." />
                    <h3 className="text-xl ...">{service.title[language]}</h3>
                    <p className="text-gray-400 ...">{service.description[language]}</p>
                  </div>
                </Link>
              );
            })}
          </div>
        </div>
      </section>

      <LearningSection language={language} onNavigate={handleNavigate} />
      <ContactSection language={language} />
    </>
  );
};

export default HomePage;
