// frontend/src/components/ServicesSection.js (VERSIÓN CON NAVEGACIÓN)

import React from 'react';
// 1. Importamos Link de React Router
import { Link } from 'react-router-dom';

// --- (Los componentes de los íconos y el array de `servicesData` se mantienen igual que en la versión anterior) ---
// ... IconConsulting, IconNetSuite, etc.
// ... const servicesData = [...]

const ServicesSection = ({ language }) => {
  // 2. YA NO NECESITAMOS LA LÓGICA DEL MODAL AQUÍ
  // const [modalIsOpen, setModalIsOpen] = useState(false);
  // const [selectedService, setSelectedService] = useState(null);
  // ... etc.

  return (
    // YA NO NECESITAMOS EL FRAGMENT <> NI EL COMPONENTE MODAL AQUÍ
    <section id="services" className="py-20 sm:py-24">
      <div className="container mx-auto px-4">
        {/* El título y la descripción se mantienen igual */}
        <h2 className="text-3xl md:text-4xl ...">
          {/* ... */}
        </h2>
        <p className="text-lg text-gray-400 ...">
          {/* ... */}
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {servicesData.map((service, index) => {
            const IconComponent = service.icon;
            return (
              // 3. ENVOLVEMOS LA TARJETA EN UN COMPONENTE <Link>
              <Link 
                to="/servicios" 
                // 4. PASAMOS EL SERVICIO SELECCIONADO A TRAVÉS DEL 'state'
                state={{ selectedServiceTitle: service.title }}
                key={index}
              >
                <div
                  className="relative bg-gray-800/50 p-8 rounded-xl h-full group transition-all duration-300 overflow-hidden cursor-pointer hover:bg-gray-700/60 hover:-translate-y-1"
                >
                  <div className="absolute inset-0 bg-gradient-to-br from-blue-500 to-cyan-400 opacity-0 group-hover:opacity-100 transition-opacity duration-300" style={{'clipPath': 'polygon(0 0, 100% 0, 100% 100%, 0 100%, 0 0, 4px 4px, 4px calc(100% - 4px), calc(100% - 4px) calc(100% - 4px), calc(100% - 4px) 4px, 4px 4px)'}}></div>
                  <div className="relative z-10">
                    <div className="mb-6">
                      <div className="w-16 h-16 ...">
                        <IconComponent className="w-8 h-8 ..." />
                      </div>
                    </div>
                    <h3 className="text-xl font-bold text-white mb-3">
                      {service.title[language]}
                    </h3>
                    <p className="text-gray-400 text-sm leading-relaxed">
                      {service.description[language]}
                    </p>
                  </div>
                </div>
              </Link>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
